// frontend/src/pages/GamePage.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { questions } from '../data/questions'; // Import mock questions data
import Lifeline from '../components/Lifeline/Lifeline'; // Import the Lifeline component
import './GamePage.css'; // Import the CSS for this page
import Modal from '../components/Modal/Modal'; // Assuming you might need a modal for game messages

/**
 * GamePage Component
 * Manages the main game logic, question display, answer submission, lifelines, and score tracking.
 * @param {Object} currentUser - The current logged-in user object { id, username, pfpUrl, team: { name, color } }.
 * @param {function} onGameEnd - Callback function to transition to the result screen, passing final score.
 * @param {function} onScoreUpdate - Callback function to update score on the backend (e.g., after each question).
 * @param {function} onNavigateToLeaderboard - Callback function to navigate to the leaderboard screen.
 */
const GamePage = ({ currentUser, onGameEnd, onScoreUpdate, onNavigateToLeaderboard }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Renamed for clarity
  const [selectedAnswerIndex, setSelectedAnswerIndex] = useState(null); // Renamed for clarity
  const [gameQuestions, setGameQuestions] = useState([]);
  const [lifelines, setLifelines] = useState({
    fiftyFifty: true,
    phoneAddy: true,
    phoneYinger: true,
    askAudience: true
  });
  const [removedAnswers, setRemovedAnswers] = useState([]); // Indices of answers removed by 50/50
  const [isAnswerSubmitted, setIsAnswerSubmitted] = useState(false); // True when an answer is selected and being processed
  const [timeRemaining, setTimeRemaining] = useState(60);
  const [timerActive, setTimerActive] = useState(false);
  const [hasUpdatedScoreForCurrentQuestion, setHasUpdatedScoreForCurrentQuestion] = useState(false); // Flag to prevent multiple score updates per question
  const [showModal, setShowModal] = useState(false); // State for showing/hiding the modal
  const [modalMessage, setModalMessage] = useState(''); // Message to display in the modal

  // Updated prize structure: Question 1 = 1,000, Question 10 = 1,000,000
  const prizeStructure = [
    1000, 2500, 5000, 10000, 25000, 50000, 100000, 250000, 500000, 1000000
  ];

  // Memoized callback for game end to prevent unnecessary re-renders
  const handleGameEndCallback = useCallback((score, reason = 'completed') => {
    console.log('ðŸŽ® Game ending with score:', score, 'Reason:', reason);
    // Pass the final score and reason to the parent (App.jsx)
    if (onGameEnd) {
      onGameEnd(score, reason);
    }
  }, [onGameEnd]);

  // Memoized callback for score update (to backend)
  const handleScoreUpdateCallback = useCallback((scoreData) => {
    console.log('ðŸ“Š Attempting to update score:', scoreData);
    if (onScoreUpdate) {
      onScoreUpdate(scoreData);
    }
  }, [onScoreUpdate]);

  // Timer effect
  useEffect(() => {
    let interval;

    if (timerActive && timeRemaining > 0 && !isAnswerSubmitted) {
      interval = setInterval(() => {
        setTimeRemaining(prevTime => {
          if (prevTime <= 1) {
            // Time's up!
            clearInterval(interval);
            setTimerActive(false);
            setIsAnswerSubmitted(true); // Prevent further interaction

            // Determine score for time-out: prize from previous question, or 0 if on first question
            const gameOverScore = currentQuestionIndex > 0 ? prizeStructure[currentQuestionIndex - 1] : 0;

            // Use setTimeout to allow state updates to settle before ending game
            setTimeout(() => {
              if (currentUser?.id) { // Use currentUser.id for backend updates
                handleScoreUpdateCallback({
                  userId: currentUser.id,
                  username: currentUser.username,
                  pfpUrl: currentUser.pfpUrl,
                  team: currentUser.team,
                  score: gameOverScore,
                  questionNumber: currentQuestionIndex + 1,
                  timeUp: true
                });
              }
              handleGameEndCallback(gameOverScore, 'time_up');
            }, 500); // Small delay before ending game

            return 0; // Set time to 0
          }
          return prevTime - 1;
        });
      }, 1000);
    }

    // Cleanup function for the interval
    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [timerActive, isAnswerSubmitted, timeRemaining, currentQuestionIndex, prizeStructure, currentUser, handleScoreUpdateCallback, handleGameEndCallback]);

  // Timer restart when moving to new question or game starts
  useEffect(() => {
    if (gameQuestions.length > 0 && gameQuestions[currentQuestionIndex] && !isAnswerSubmitted) {
      setTimeRemaining(60); // Reset timer for each new question
      setTimerActive(true);
      setHasUpdatedScoreForCurrentQuestion(false); // Reset score update flag for new question
    } else if (isAnswerSubmitted) {
      setTimerActive(false); // Ensure timer stops if answer is submitted
    }
  }, [currentQuestionIndex, gameQuestions.length, isAnswerSubmitted]);

  // Initialize game questions on component mount
  useEffect(() => {
    console.log('ðŸŽ¯ Initializing game questions...');
    // Select 10 random questions from the pool of 30
    const shuffled = [...questions].sort(() => Math.random() - 0.5);
    const selected = shuffled.slice(0, 10);
    setGameQuestions(selected);
    setCurrentQuestionIndex(0); // Start from the first question
    setSelectedAnswerIndex(null);
    setRemovedAnswers([]);
    setIsAnswerSubmitted(false);
    setLifelines({
      fiftyFifty: true,
      phoneAddy: true,
      phoneYinger: true,
      askAudience: true
    });
    setHasUpdatedScoreForCurrentQuestion(false); // Reset score update flag
  }, []); // Empty dependency array means this runs once on mount

  // Update leaderboard/score on backend when moving to a new question (or game start)
  // This effect runs when currentQuestionIndex changes, ensuring score is updated for the *new* question
  useEffect(() => {
    // Only update if questions are loaded, user is available, and score hasn't been updated for this question yet
    if (gameQuestions.length > 0 && currentUser?.id && !hasUpdatedScoreForCurrentQuestion) {
      // The score to update is the prize they *currently have* (secured) from the *previous* question.
      // If they are on question 1 (index 0), their secured score from previous questions is 0.
      // If they are on question 2 (index 1), their secured score is prizeStructure[0].
      const scoreToUpdate = currentQuestionIndex > 0 ? prizeStructure[currentQuestionIndex - 1] : 0;

      console.log(`ðŸ“ˆ Score update for entering question ${currentQuestionIndex + 1}, secured score: ${scoreToUpdate}`);

      handleScoreUpdateCallback({
        userId: currentUser.id,
        username: currentUser.username,
        pfpUrl: currentUser.pfpUrl,
        team: currentUser.team,
        score: scoreToUpdate,
        questionNumber: currentQuestionIndex + 1, // Indicate which question they are *on*
        gameStatus: 'in_progress' // Indicate game is ongoing
      });

      setHasUpdatedScoreForCurrentQuestion(true); // Mark as updated
    }
  }, [currentQuestionIndex, gameQuestions, currentUser, handleScoreUpdateCallback, hasUpdatedScoreForCurrentQuestion, prizeStructure]);


  const handleAnswerSelect = useCallback((answerIndex) => {
    // Don't allow selection of removed answers or if answer already submitted
    if (removedAnswers.includes(answerIndex) || isAnswerSubmitted) return;
    setSelectedAnswerIndex(answerIndex);
  }, [removedAnswers, isAnswerSubmitted]);

  // Submit Answer function
  const submitAnswer = useCallback(() => {
    if (selectedAnswerIndex === null || !gameQuestions[currentQuestionIndex] || isAnswerSubmitted) {
      setModalMessage('Please select an answer before submitting.');
      setShowModal(true);
      return;
    }

    console.log(`ðŸŽ¯ Submitting answer ${selectedAnswerIndex} for question ${currentQuestionIndex + 1}`);
    setIsAnswerSubmitted(true); // Lock selections and timer
    setTimerActive(false); // Stop the timer immediately

    const currentQuestionData = gameQuestions[currentQuestionIndex];
    const correct = currentQuestionData.correctAnswer === selectedAnswerIndex;

    // Show result for 2 seconds before proceeding
    setTimeout(() => {
      if (correct) {
        setModalMessage('âœ… Correct Answer!');
        setShowModal(true);

        if (currentQuestionIndex === 9) {
          // Game completed successfully (answered all 10 questions)
          const finalScore = prizeStructure[currentQuestionIndex];
          console.log('ðŸŽ‰ Game completed! Final score:', finalScore);
          if (currentUser?.id) {
            handleScoreUpdateCallback({
              userId: currentUser.id,
              username: currentUser.username,
              pfpUrl: currentUser.pfpUrl,
              team: currentUser.team,
              score: finalScore,
              questionNumber: currentQuestionIndex + 1,
              completed: true,
              gameStatus: 'finished'
            });
          }
          handleGameEndCallback(finalScore, 'win'); // Pass final score and win reason
        } else {
          // Move to next question
          console.log(`âœ… Correct answer! Moving to question ${currentQuestionIndex + 2}`);
          setCurrentQuestionIndex(prev => prev + 1);
          setSelectedAnswerIndex(null);
          setRemovedAnswers([]);
          setIsAnswerSubmitted(false);
          setHasUpdatedScoreForCurrentQuestion(false); // Allow score update for next question
          // Timer will restart automatically due to useEffect
        }
      } else {
        // Wrong answer - end game
        setModalMessage('âŒ Wrong Answer! Game Over.');
        setShowModal(true);

        // Score is the prize from the *previous* question (or 0 if on first question)
        const gameOverScore = currentQuestionIndex > 0 ? prizeStructure[currentQuestionIndex - 1] : 0;
        console.log('âŒ Wrong answer! Game over with score:', gameOverScore);
        if (currentUser?.id) {
          handleScoreUpdateCallback({
            userId: currentUser.id,
            username: currentUser.username,
            pfpUrl: currentUser.pfpUrl,
            team: currentUser.team,
            score: gameOverScore,
            questionNumber: currentQuestionIndex + 1,
            failed: true,
            gameStatus: 'game_over'
          });
        }
        handleGameEndCallback(gameOverScore, 'lose'); // Pass final score and lose reason
      }
    }, 2000); // Show feedback for 2 seconds
  }, [selectedAnswerIndex, gameQuestions, currentQuestionIndex, isAnswerSubmitted, prizeStructure, currentUser, handleScoreUpdateCallback, handleGameEndCallback]);

  // Callback to handle lifeline usage from the Lifeline component
  const handleLifelineUse = useCallback((type) => {
    console.log('Using lifeline:', type);
    setLifelines(prev => ({ ...prev, [type]: false }));

    const currentQuestionData = gameQuestions[currentQuestionIndex];
    if (!currentQuestionData) return;

    if (type === 'fiftyFifty') {
      const incorrectAnswers = currentQuestionData.options
        .map((_, i) => i)
        .filter(i => i !== currentQuestionData.correctAnswer);

      // Randomly select two incorrect answers to remove
      const answersToRemove = [];
      while (answersToRemove.length < 2 && incorrectAnswers.length > 0) {
        const randomIndex = Math.floor(Math.random() * incorrectAnswers.length);
        answersToRemove.push(incorrectAnswers.splice(randomIndex, 1)[0]);
      }
      setRemovedAnswers(answersToRemove);
      setModalMessage('50/50 used! Two incorrect answers have been removed.');
      setShowModal(true);
    } else if (type === 'phoneAddy' || type === 'phoneYinger') {
      // For Phone Addy/Yinger, reveal the correct answer
      setModalMessage(`Your friend says the correct answer is: ${String.fromCharCode(65 + currentQuestionData.correctAnswer)} (${currentQuestionData.options[currentQuestionData.correctAnswer]})`);
      setShowModal(true);
    } else if (type === 'askAudience') {
      // Simulate audience votes
      const votes = {};
      const correctAnswerIndex = currentQuestionData.correctAnswer;
      const totalVotes = 100;
      let remainingVotes = totalVotes;

      // Give correct answer a significant portion
      const correctVotes = Math.floor(Math.random() * (60 - 40 + 1)) + 40; // 40-60%
      votes[correctAnswerIndex] = correctVotes;
      remainingVotes -= correctVotes;

      // Distribute remaining votes among other answers
      const otherOptions = currentQuestionData.options.map((_, i) => i).filter(i => i !== correctAnswerIndex);
      const numOtherOptions = otherOptions.length;

      otherOptions.forEach((optionIndex, idx) => {
        if (idx === numOtherOptions - 1) { // Last option gets remaining votes
          votes[optionIndex] = remainingVotes;
        } else {
          const voteShare = Math.floor(Math.random() * (remainingVotes / (numOtherOptions - idx) * 1.5));
          votes[optionIndex] = voteShare;
          remainingVotes -= voteShare;
        }
      });

      // Format for display
      let audienceMessage = 'Audience votes:\n';
      currentQuestionData.options.forEach((option, index) => {
        audienceMessage += `${String.fromCharCode(65 + index)}: ${votes[index] || 0}%\n`;
      });
      setModalMessage(audienceMessage);
      setShowModal(true);
    }
  }, [gameQuestions, currentQuestionIndex]);


  // Walk Away function
  const walkAway = useCallback(() => {
    // Only allow walk away if not on the first question
    if (currentQuestionIndex === 0) {
      setModalMessage('You cannot walk away on the first question. You must answer!');
      setShowModal(true);
      return;
    }

    setTimerActive(false); // Stop the timer
    setIsAnswerSubmitted(true); // Prevent further interaction

    // Player walks away with the prize from the *previous* question they successfully answered
    const currentPrizeSecured = prizeStructure[currentQuestionIndex - 1];
    console.log('ðŸš¶ Player walking away with:', currentPrizeSecured);

    if (currentUser?.id) {
      handleScoreUpdateCallback({
        userId: currentUser.id,
        username: currentUser.username,
        pfpUrl: currentUser.pfpUrl,
        team: currentUser.team,
        score: currentPrizeSecured,
        questionNumber: currentQuestionIndex, // Indicate they walked away *before* answering this question
        walkedAway: true,
        gameStatus: 'walked_away'
      });
    }
    handleGameEndCallback(currentPrizeSecured, 'walk_away'); // Pass score and walk away reason
  }, [currentQuestionIndex, prizeStructure, currentUser, handleScoreUpdateCallback, handleGameEndCallback]);

  // Handle leaderboard navigation with logging
  const handleLeaderboardClick = useCallback(() => {
    console.log('ðŸ† Navigating to leaderboard...');
    if (onNavigateToLeaderboard) {
      onNavigateToLeaderboard();
    } else {
      console.error('âŒ onNavigateToLeaderboard function not provided');
      setModalMessage('Leaderboard navigation is not available.');
      setShowModal(true);
    }
  }, [onNavigateToLeaderboard]);

  // Memoized timer functions for dynamic styling
  const getTimerColor = useCallback(() => {
    if (timeRemaining > 30) return '#B0FF6F'; // Green
    if (timeRemaining > 15) return '#FF955E'; // Orange
    return '#FF54D7'; // Pink/Red
  }, [timeRemaining]);

  const getTimerText = useCallback(() => {
    if (timeRemaining > 30) return 'ðŸŸ¢';
    if (timeRemaining > 15) return 'ðŸŸ¡';
    return 'ðŸ”´';
  }, [timeRemaining]);

  // Early return for loading state or if questions aren't loaded yet
  if (gameQuestions.length === 0 || !gameQuestions[currentQuestionIndex]) {
    return (
      <div className="game-loading-screen">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <div className="loading-text">Loading questions...</div>
        </div>
      </div>
    );
  }

  const currentQuestionData = gameQuestions[currentQuestionIndex];
  const currentPrizePotential = prizeStructure[currentQuestionIndex]; // Prize for answering *this* question correctly
  const timerColor = getTimerColor();
  const timerText = getTimerText();

  // Debug log for current state
  console.log('ðŸŽ® GamePage Render:', {
    questionNumber: currentQuestionIndex + 1,
    currentPrizePotential,
    timeRemaining,
    timerActive,
    isAnswerSubmitted,
    hasUpdatedScore: hasUpdatedScoreForCurrentQuestion,
    currentUser: currentUser?.username,
    currentTeam: currentUser?.team?.name,
  });

  return (
    <div className="game-page-container"> {/* Main container */}
      {/* Background Animation */}
      <div className="background-animation">
        <div className="floating-shapes">
          {[...Array(20)].map((_, i) => (
            <div key={i} className={`shape shape-${i % 4}`}></div>
          ))}
        </div>
      </div>

      <div className="game-content-wrapper"> {/* Wrapper for main game content (header, main-game-area) */}
        {/* Header */}
        <div className="game-header">
          <div className="user-info">
            <img
              src={currentUser?.pfpUrl || 'https://placehold.co/50x50/cccccc/000000?text=PFP'}
              alt="Profile"
              className="user-pfp"
              onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/50x50/cccccc/000000?text=PFP'; }}
            />
            <span className="user-username">{currentUser?.username || 'User'}</span>
            <div
              className="user-team"
              style={{ backgroundColor: currentUser?.team?.color || '#000' }}
            >
              {currentUser?.team?.name || 'Team'}
            </div>
          </div>

          <div className="score-info">
            <div className="current-prize">
              {currentPrizePotential?.toLocaleString() || '0'} $PROVE
            </div>
            <div className="question-number">
              Question {currentQuestionIndex + 1} of 10
            </div>
          </div>

          {/* Header Actions */}
          <div className="header-actions">
            {/* Leaderboard Button */}
            <button
              className="leaderboard-button"
              onClick={handleLeaderboardClick}
              type="button"
              title="View Leaderboard"
            >
              <span className="leaderboard-icon">ðŸ†</span>
              <span className="leaderboard-text">Leaderboard</span>
            </button>

            {/* Compact Lifelines and Timer Section */}
            <div className="lifelines-timer-section">
              {/* Timer Display */}
              <div
                className={`timer-display ${timeRemaining <= 10 ? 'timer-critical' : ''}`}
                style={{
                  color: timerColor,
                  borderColor: timerColor,
                  boxShadow: `0 0 10px ${timerColor}80` // Adjusted shadow for better visibility
                }}
              >
                {timerText} {timeRemaining}s
              </div>
            </div>
          </div>
        </div>

        {/* Main Game Layout: Lifelines (Left) | Question (Center) | Prize Ladder (Right) */}
        <div className="main-game-layout">
          {/* Lifelines on the left */}
          <Lifeline
            lifelines={lifelines}
            onLifelineUse={handleLifelineUse}
            currentQuestion={currentQuestionData} // Pass the current question object
            isAnswerSubmitted={isAnswerSubmitted}
            onRemovedAnswers={setRemovedAnswers} // Direct setter for simplicity
          />

          {/* Question in the center */}
          <div className="question-container">
            <div className="question-card">
              <h2 className="question-text">
                {currentQuestionData.question}
              </h2>

              <div className="answers-grid">
                {currentQuestionData.options.map((option, index) => (
                  <button
                    key={index}
                    className={`answer-button ${selectedAnswerIndex === index ? 'selected' : ''} ${removedAnswers.includes(index) ? 'removed' : ''} ${isAnswerSubmitted ? (index === currentQuestionData.correctAnswer ? 'correct' : (selectedAnswerIndex === index ? 'incorrect' : '')) : ''}`}
                    onClick={() => handleAnswerSelect(index)}
                    disabled={removedAnswers.includes(index) || isAnswerSubmitted}
                    type="button"
                  >
                    <span className="answer-letter">{String.fromCharCode(65 + index)}</span>
                    <span className="answer-text">{option}</span>
                  </button>
                ))}
              </div>

              <div className="action-buttons">
                <button
                  className="walk-away-button"
                  onClick={walkAway}
                  disabled={isAnswerSubmitted || currentQuestionIndex === 0}
                  type="button"
                >
                  Walk Away
                  {currentQuestionIndex > 0 && (
                    <span className="walk-away-amount">
                      ({prizeStructure[currentQuestionIndex - 1]?.toLocaleString()} $PROVE)
                    </span>
                  )}
                </button>

                <button
                  className="submit-button"
                  onClick={submitAnswer}
                  disabled={selectedAnswerIndex === null || isAnswerSubmitted}
                  type="button"
                >
                  {isAnswerSubmitted ? 'Processing...' : 'Final Answer'}
                </button>
              </div>
            </div>
          </div>

          {/* Prize Ladder on the right */}
          <div className="prize-ladder">
            <div className="prize-ladder-header">
              <h4 className="prize-ladder-title">ðŸ’° PRIZE LADDER</h4>
            </div>
            {[...prizeStructure].reverse().map((prize, reverseIndex) => {
              const actualIndex = prizeStructure.length - 1 - reverseIndex;
              return (
                <div
                  key={actualIndex}
                  className={`prize-item ${actualIndex === currentQuestionIndex ? 'current' : ''} ${actualIndex < currentQuestionIndex ? 'completed' : ''}`}
                >
                  <span className="prize-number">{actualIndex + 1}</span>
                  <span className="prize-amount">{prize.toLocaleString()} $PROVE</span>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      {/* Modal for game messages */}
      <Modal show={showModal} onClose={() => setShowModal(false)} message={modalMessage} />
    </div>
  );
};

export default GamePage;
